CREATE  VIEW `jos_emundus_stats_candidature_submit` AS select count(`jos_emundus_campaign_candidature`.`fnum`) AS `countCandidature`,cast(`jos_emundus_campaign_candidature`.`date_submitted` as date) AS `date`,`jos_emundus_setup_campaigns`.`label` AS `campaign` from (`jos_emundus_campaign_candidature` left join `jos_emundus_setup_campaigns` on((`jos_emundus_campaign_candidature`.`campaign_id` = `jos_emundus_setup_campaigns`.`id`))) where ((`jos_emundus_campaign_candidature`.`published` <> -(1)) and (cast(`jos_emundus_campaign_candidature`.`date_submitted` as date) <> '0000-00-00')) group by cast(`jos_emundus_campaign_candidature`.`date_submitted` as date),`jos_emundus_setup_campaigns`.`label` order by cast(`jos_emundus_campaign_candidature`.`date_submitted` as date)