CREATE  VIEW `jos_emundus_stats_nombre_comptes` AS select uuid() AS `id`,count(`eu`.`profile`) AS `nombre`,date_format(`eu`.`registerDate`,'%Y%m%d') AS `_date`,date_format(`eu`.`registerDate`,'%Y-%m-%d') AS `_day`,date_format(`eu`.`registerDate`,'%u') AS `_week`,date_format(`eu`.`registerDate`,'%b') AS `_month`,date_format(`eu`.`registerDate`,'%Y') AS `_year`,`sp`.`id` AS `profile_id`,`sp`.`label` AS `profile_label` from (`jos_emundus_users` `eu` left join `jos_emundus_setup_profiles` `sp` on((`sp`.`id` = `eu`.`profile`))) where `eu`.`profile` in (select `jos_emundus_setup_profiles`.`id` from `jos_emundus_setup_profiles` where (`jos_emundus_setup_profiles`.`published` = 1)) group by `eu`.`profile`,date_format(`eu`.`registerDate`,'%Y%m%d')